var zeus = zeus || {};
zeus.tabletBreak = 980;
zeus.phoneBreak = 700;
zeus.rrTablet = false;

/*
 * Mobile Menu
 */
zeus.mainMenuMobile = function() {

  zeus.mobileEnabled = true;

  // Swipe menu closed, when we're on the appropriate device
  zeus.swipeElement.swipe({
    swipeLeft: function() {
      jQuery.sidr('close', 'tablet-mobile-menu');
    }
  });

  // Click outside menu to close
  jQuery('#zeusmain').click(function() {
    jQuery.sidr('close', 'tablet-mobile-menu');
  });

  // Close menu if in desktop mode
  jQuery(window).resize(function() {
    var winWidth = jQuery(window).width();
    if (winWidth >= zeus.tabletBreak) {
      jQuery.sidr('close', 'tablet-mobile-menu');
    }
  });

  // Flyout Menu
  // sidr won't call the open/close event handler if it's in an object (zeus.)
  jQuery('#menu-btn').sidr({
    name: 'tablet-mobile-menu',
    side: 'left',
    displace: true,
    onOpen: subMenuEvent,
    onClose: closeMenuEvent
  });

}

/*
 * Desktop Menu
 */
zeus.mainMenuDesktop = function() {
  // Click outside menu to close
  jQuery('#zeusmain').click(function() {
    jQuery.sidr('close', 'tablet-mobile-menu');
  });


  // Close menu if in desktop mode
  jQuery(window).resize(function() {
    var winWidth = jQuery(window).width();
    if (winWidth >= zeus.tabletBreak) {
      jQuery.sidr('close', 'tablet-mobile-menu');
    }
  });

  // Flyout Menu
  // sidr won't call the open/close event handler if it's in an object (zeus.)
  jQuery('#menu-btn').sidr({
    name: 'tablet-mobile-menu',
    side: 'left',
    displace: true,
    onOpen: subMenuEvent,
    onClose: closeMenuEvent
  });

}

/*
 * Interstitial Ad
*/
zeus.interstitialAd = function() {
    // Vars
    var timer;
    var timeComplete = 0;
    var header = "#header";
    var interAd = ".region-banner-ad";
    var leaderBoard = jQuery(interAd).outerHeight();
    var headerHeight = jQuery(header).outerHeight();
    var headerLeader = leaderBoard + headerHeight;
    if (leaderBoard > 25) {
        jQuery('#page').css("padding-top", headerLeader);
        jQuery(interAd).addClass('scrolling loaded');
        jQuery(header).removeClass('scrolling');
        jQuery(header).addClass('leader');
        if (!jQuery("body").hasClass("node-type-slideshow")) {
            if (timeComplete === 0) {
                if ( timer ) clearTimeout(timer);
                timer = setTimeout(function(){
                    //do something after 1 second
                    var windowPosition = jQuery(window).scrollTop();
                    if (windowPosition > leaderBoard){
                        jQuery(header).animate({top: "-=" + leaderBoard + "px", },400);
                        jQuery(interAd).slideUp(400, function () {
                            jQuery(interAd).removeClass('scrolling');
                            jQuery(header).removeClass('leader'); // replace with animation of top css property
                            jQuery('#page').css("padding-top", headerHeight + "px");
                            jQuery(interAd).show();
                            jQuery(header).addClass('scrolling');
                            jQuery(header).css("top", 0);
                        });
                    } else {
                        jQuery(interAd).removeClass('scrolling');
                        jQuery('#page').css("padding-top", 0);
                        jQuery(header).removeClass('leader');
                    }
                    timeComplete = 1;
                    zeus.scrollingFixedHeader();
                }, 5000); // change value of time you want the AD to display 1000 = 1sec
            }
        }
    } else {
        jQuery(header).addClass('scrolling'); //enables scrolling when leaderboard is not needed
        adminMenuAdjust();
    }
}


/*
 * Add space above fixed header to accomodate admin-menu
 */
function adminMenuAdjust() {
    if(jQuery('body').hasClass('admin-menu')) {
        var adminMenuHeight = jQuery('#admin-menu').outerHeight();
        jQuery('body.admin-menu #header.scrolling').css("top", adminMenuHeight);
    }
}

/*
 * Main Menu Mobile Functions
 */
function closeMenuEvent() {
  // Vars
  var firstLevelMenuItem = '#tablet-mobile-menu ul.menu li a';
  var page = jQuery("body, html");

  jQuery('#menu-btn-toggle').removeClass('open');
  jQuery(firstLevelMenuItem).parent().removeClass('active-open');

  //TODO Change the skin position only when present
  jQuery(page).css("background-position", "center top").css("position", "initial");
}

function subMenuEvent() {

  // Vars
  var firstLevelMenuItem = '#tablet-mobile-menu ul.menu li a';
  var page = jQuery("body, html");

  jQuery('#menu-btn-toggle').addClass('open');

  //Locks the page scroll so only the hamburger can scroll
  jQuery(page).css({overflow: "visible", position: "fixed"});

  // Show/hide secondary menu
  jQuery(firstLevelMenuItem).click(function(event) {

    if (jQuery(this).parent().hasClass('expanded')) {
      // Prevent firstLevelMenu items from firing
      event.preventDefault();
      // Set/remove active class
      jQuery(this).parent().toggleClass('active-open');
    }
  });
  //TODO Change the skin position only when present
  jQuery(page).css("background-position", "0px top");
}

/*
* Scrolling fixed header
*/
zeus.scrollingFixedHeader = function() {

    // Vars
    var header = "#header";
    var interAd = ".region-banner-ad";
    var headerHeight = jQuery(header).outerHeight();

    // Scroll function
    jQuery(window).scroll(function() {

        // Vars
        var windowPosition = jQuery(window).scrollTop();
        var leaderBoard = jQuery(interAd).outerHeight();
        var offset = leaderBoard; // used to offset the bars above the nav so the sticky doesn't happen immediately

        // Accounts for admin menu
        adminMenuAdjust();

        // Add scrolling class
        if (windowPosition > offset) {
            jQuery(header).addClass('scrolling');
            jQuery('#page').css("padding-top", headerHeight);
        } else {
            jQuery(header).removeClass('scrolling');
            jQuery('#page').css("padding-top", 0);
            jQuery(header).css("top", 0);
        }

    });
}

// handles sticky right rail when infinite scroll is disabled
zeus.stickyRightRailHandler = function() {
    var top = '#header';    // top most point for rr
    var bottom = '#bottom'; // bottom most point for rr
    var contentItem = jQuery('#itemsContainer .scroll-item'); // outer element for this content (Article/slideshow)
    var el = contentItem.find('.right_rail .inner');
    var headerHeight = jQuery(top).outerHeight();
    if (jQuery("body").hasClass("node-type-slideshow")) {
        headerHeight += jQuery('.region-banner-ad').outerHeight(); //add non-collapsing banner height for slideshow
    }
    var extraSpace = 20;
    var limitN = 0;
    var diff = 0;
    var limit = 0;
    var stickyRRDuration = 0;

    // if rr present for this content
    if (el.length) {
        var stickyTop = el.offset().top;
        var win = jQuery(window);

        // On scroll behaviour for RR
        win.bind("scroll.rightrail", function() {
            var stickyHeight = el.height();
            var skyboxHeight = jQuery('.OUTBRAIN.ob-shrink').outerHeight();
            var extraMargin = headerHeight + extraSpace;
            // if we have reached bottom and there is no timeout,
            // always calculate fresh limit as outbrain and images load slow or the right and bottom outbrain module collides
            if (typeof jQuery.data(el[0], 'timeout') === 'undefined' || typeof jQuery.data(el[0], 'limit') === 'undefined') {
                limit = contentItem.find(bottom).offset().top - stickyHeight - extraMargin;
            } else {
                limit = jQuery.data(el[0], 'limit');
            }
            var windowTop = win.scrollTop() + extraMargin;
            // Element has not reached the top of window
            if (stickyTop + skyboxHeight > windowTop) {
                // remove sticky as rr  has scrolled to orig pos
                if (el.hasClass('sticky')) {
                    el.css({ top: "" });
                    el.removeClass('sticky');
                    // remove all set data for rr and start afresh
                    jQuery.removeData(el[0], 'limit');
                    jQuery.removeData(el[0], 'return');
                    jQuery.removeData(el[0], 'timeout');
                }
            }
            else {
                // Element has reached the limit (maybe timeout or bottom of content)
                if (limit < windowTop) {
                    diff = (limit - windowTop) + extraMargin;
                    el.css({ top: diff});
                    el.addClass('sticky');
                    jQuery.data(el[0], 'return', 1);                        // rr returning to top of content
                } else {
                    // element has reached top of window and has not yet reached the limit
                    // so set it STICKY
                    el.css({ top: extraMargin });
                    // set the right rail fixed to the top
                    if (!el.hasClass('sticky')) {
                        el.addClass('sticky');
                        if (stickyRRDuration > 0) {
                            // for duration based stickiness
                            setTimeout(function() {
                                // when the timeout is called, first check if the element
                                // has already reached the bottom limit
                                // in that case this is not needed
                                if (typeof jQuery.data(el[0], 'return') === 'undefined') {
                                    limitN =  el.offset().top;
                                    diff = (limitN - windowTop);
                                    //el.css({ top: diff});
                                    jQuery.data(el[0], 'return', 1);            // rr returning to top of content
                                    jQuery.data(el[0], 'timeout', 1);           // timeout for sticky
                                    jQuery.data(el[0], 'limit', limitN);
                                }
                            }, stickyRRDuration);
                        }
                    }
                }
            }
        });
    }
}

/*
 * Header Search Toggle
 */
zeus.headerSearchToggle = function() {

  // Vars
  var searchForm = '#header #zeus-search-field-form';
  var searchIconBtn = '#header .search-submit';
  var searchInput = '#header #search-field';
  var timeout;

  // Close search field
  zeus.headerSearchToggle.closeSearchField = function () {

      jQuery(searchIconBtn).removeClass("active");

      //jQuery(searchInput).focus();
      jQuery(searchInput).animate({
        width: 0,
        opacity: 0,
      }, {
        duration: "slow"
      });
      setTimeout(function () {
            jQuery(searchInput).hide();
      }, 500); //hides field entirely after animation completes
  }

  // Submit Form
  zeus.headerSearchToggle.submitSearchForm = function () {

    var inputVal = jQuery(searchInput).val();
    // Submit form
    jQuery(searchForm).submit();
  }

  // On search icon click
  jQuery(searchIconBtn).click( function(event) {

    if(jQuery(searchIconBtn).hasClass("active")) {
    // Search field is already opened

    var inputVal = jQuery(searchInput).val();
    if(inputVal == "") {
      // blank search input, so close the field on click
      zeus.headerSearchToggle.closeSearchField();
    } else {
      // Run submit and goto search page
      zeus.headerSearchToggle.submitSearchForm();
    }
    } else {
      // Search field is not open, so open it on click of search icon
      jQuery(searchIconBtn).addClass("active");
      //jQuery(searchInput).focus();
      jQuery(searchInput).show();
      jQuery(searchInput).animate({
        width: 250,
        opacity: 1
      }, {
        duration: "slow"
      });
      jQuery(searchInput).focus();
    }
    // Disable Link
    event.preventDefault();
  });
}

/*
 * Make full-width blocks window width
 */
zeus.fullWidthBlocks = function() {
    var timer;
    var contentItem = '.fullwidth-content, .full-width__inner';

    // set width on ready
    setContentWidth(contentItem);

    // reset width on browser resize
    // timer throttles reset for performance
    jQuery(window).resize(function() {
      if(timer) {
        window.clearTimeout(timer);
      }
      timer = window.setTimeout(function() {
        setContentWidth(contentItem);
      }, 100);
    });
}

/*
 * Set item width to window width
 */
function setContentWidth(contentItem) {
  var w = jQuery(window).width();
  if (w < 685) {
      jQuery(contentItem).css('width', 'auto');
  } else {
      jQuery(contentItem).css('width', w);
  }
}

/*
 * Workout center expandable.
 */
zeus.wc_accordion = function() {
  jQuery('.wc-expand').on('click', function(e){
    e.preventDefault();
    var workoutExtra = jQuery(this).siblings('.wc-exercise-extra');
    if(jQuery(this).hasClass('expanded')){
      jQuery(this).removeClass('expanded');
      jQuery(workoutExtra).slideUp();
    }
    else{
      jQuery(this).addClass('expanded');
      jQuery(workoutExtra).slideDown();
    }
  });
};
zeus.wc_masonry = function(){
   jQuery('.masonry').masonry({
    itemSelector: '.card',
    columnWidth: '.grid-sizer',
    gutter: '.gutter-sizer',
    percentPosition: true
  });
}

jQuery(window).load(function() {
  zeus.interstitialAd(); //this calls scrollingFixedHeader after AD function completes

  // if infinite scroll is not enabled
  if (typeof Drupal.settings.zeus_utils_load_more === "undefined") {

    var winWidth = jQuery(window).width();
    if (typeof zeus.tabletBreak === 'undefined') {
        zeus.tabletBreak = 980;
    }
    // right rail
    if (winWidth >= zeus.tabletBreak) {
        // a fix for firefox where the sticky right rail shifting to extreme right on first article
        var rr = jQuery('#itemsContainer .right_rail');
        var inr = rr.find('.inner');
        rr.css({'width': inr.css('width'), 'height' : inr.css('height')});
        // sticky RR
        zeus.stickyRightRailHandler();
    }
  }
});

