/**
 * Minimum character counter
 */
zeus.minCharCount = function(el, minLength) {
    var id = el.id;
    if (typeof minLength === 'undefined') {
        minLength = 70;
    }
    var charCountStr = 'Content minimum is ' + minLength +' characters, remaining: ';
    var charCount;
    var charCounterId = id + '-counter';
    var charCounterEl = document.getElementById(charCounterId);
    if (charCounterEl == null) {
        jQuery(el).after('<div id="' + charCounterId + '" class="counter"></div>');
    }
    
    if (el) {
        if(el.value.length < minLength) {
            charCount = (minLength - el.value.length);
        } else {
            charCount = 0;
        }
        jQuery('#'+charCounterId).html(charCountStr + charCount);
    }
    return true;
}
